import React from 'react'
import { useDrag } from 'react-dnd'

function Hat({ id, name }) {
    const [{ isDragging }, dragRef] =({
        type: 'hat',
        item: {id, name},
        collect: (monitor) => ({
            isDragging: monitor.isDragging()
        })
    })
    return (
        <div className='hat-obj' ref={dragRef}>

        </div>
    );
}

export default Hat;
